    modelAddGroup(name, parent) {
        const data = { name: name, parent: null, objs: [], points: [] };
        if (parent !== undefined && this._groups.has(parent)) {
            data.parent = parent;
        }
        this._groups.set(name, data);
        this._topos_trees.set(name, new TopoTree());
        return data;
    }
